for i =1
    X_ori=xlsread('C:\Users\lenovo\Desktop\result_3.xlsx','Output_b','G88:G103');
    Y_ori=xlsread('C:\Users\lenovo\Desktop\result_3.xlsx','Output_b','H88:H103');
